import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthService } from '../../services/auth.service';
import { Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  loginForm!:FormGroup;
  constructor(private fb:FormBuilder,private loginService:AuthService,private router: Router,private login:) { }

  ngOnInit(): void {
    this.loginForm=this.initForm();
    //this.toastr.success('Hello world!', 'Toastr fun!');

  }
  initForm(){
    return this.fb.group({
      email:['',[Validators.email,Validators.required]],
      contraseña:['',[Validators.required]]
    })
  }

  login(){
    if(this.loginForm.valid){
  this.loginService.login(this.loginForm.value).subscribe(
    (respuesta:any)=>{
    if(respuesta){
     localStorage.setItem('token',respuesta.token)
     this.router.navigate(['/laboratorio/examenes'])
     localStorage.setItem("role",respuesta.user.role)
     localStorage.setItem("logueado",'true')

    }
    },
    (error) => {
      console.error('Error en la solicitud:', error); // Manejo de errores, imprime en caso de que ocurra algún error
    }
  );

    }
  }

}
