import { Component } from '@angular/core';
import { ExamenService } from '../services/examen.service';

@Component({
  selector: 'app-registrar-resultado',
  templateUrl: './registrar-resultado.component.html',
  styleUrls: ['./registrar-resultado.component.css']
})
export class RegistrarResultadoComponent {

  constructor(private examen:ExamenService) { }

selectedFile?: File;

onFileSelected(event:any) {
  this.selectedFile = <File>event.target.files[0];

}


onUpload() {
  if (this.selectedFile) {
  const formData = new FormData();
  formData.append('file', this.selectedFile, this.selectedFile?.name);
  this.examen.uploadexam()
}
}

}
<div class="register-photo">
  <div class="form-container">
      <div class="image-holder"></div>
      <form >
          <h2 class="text-center"><strong>Registra</strong> Resultado</h2>

          <div class="form-group">
            <input class="form-control" type="text" name="id" placeholder="Registrar id_resultado" formControlName="id">

          </div>

          <div class="form-group">
            <input class="form-control" type="text" name="name" required id="name" placeholder="Registrar id_paciente"  formControlName="name">

          </div>


          <div class="form-group">
            <label for="pdfFile">Subir archivo PDF:</label>
            <input  (change)="onFileSelected($event)" type="file" class="form-control-file" id="pdfFile" name="pdfFile" accept=".pdf">
          </div>
          <button (click)="onUpload()"  type="submit" class="btn btn-primary">Subir PDF</button>


          <div class="form-group"><button class="btn btn-primary btn-block" type="submit">Registrar Resultado</button></div></form>
  </div>
</div>
