import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Admin } from 'src/app/interface/administrador/admin';
import { ExamenService } from '../services/examen.service';

@Component({
  selector: 'app-registrar-examen',
  templateUrl: './registrar-examen.component.html',
  styleUrls: ['./registrar-examen.component.css']
})
export class RegistrarExamenComponent {



  register!:FormGroup;
  //data?:SingUp;
  constructor(private readonly fb:FormBuilder, private examenServices:ExamenService) { }

  ngOnInit(): void {
    this.register=this.initForm();
  }
  initForm():FormGroup{
  return this.fb.group({
  name:['',[Validators.required,Validators.minLength(12)]],
  id:['',[Validators.required,Validators.minLength(10)]],
  function_description:['',[Validators.required,Validators.maxLength(30)]],
  });
  }

  sendData(){
   console.log(this.register.valid)
  if(this.register.valid){

    const data: Admin = {
      id: this.register.value.id,
      name: this.register.value.name,
      function_description: this.register.value.function_description
    };

    this.examenServices.registerExam(data).subscribe(
      (
        respuesta
      )=>{this.router.navigate(['/laboratorio/examenes'])}
    )
  }
  }
}
<div class="register-photo">
  <div class="form-container">
      <div class="image-holder"></div>
      <form [formGroup]="register" (submit)="sendData()">
          <h2 class="text-center"><strong>Registra</strong> Examen</h2>

          <div class="form-group">
            <input class="form-control" type="text" name="id" placeholder="Registrar id_examen" formControlName="id">
            <div *ngIf="register.get('id')?. touched && register.get('id')?.errors?.['required']" class="alert alert-danger">Registrar id_examen</div>
            <div *ngIf="register.get('id')?. touched && register.get('id')?.errors?.['minlength']" class="alert alert-danger">Minimo son {{register.get('id')?.errors?.['minlength']?.requiredLength}} caracteres</div>
          </div>

          <div class="form-group">
            <input class="form-control" type="text" name="name" required id="name" placeholder="Nombre del examen"  formControlName="name">
            <div *ngIf="register.get('name')?. touched && register.get('name')?.errors?.['required']" class="alert alert-danger">Nombre del examen</div>
            <div *ngIf="register.get('name')?. touched && register.get('name')?.errors?.['minlength']" class="alert alert-danger">Minimo son {{register.get('name')?.errors?.['minlength']?.requiredLength}} caracteres</div>
          </div>


          <div class="form-group">
            <textarea class="form-control" type="text" placeholder="descripcion del examen" formControlName="function_description"> </textarea>
            <div *ngIf="register.get('name')?. touched && register.get('name')?.errors?.['required']" class="alert alert-danger">Campos requerido</div>
            <div *ngIf="register.get('name')?. touched && register.get('name')?.errors?.['minlength']" class="alert alert-danger">Maximo son {{register.get('name')?.errors?.['minlength']?.requiredLength}} caracteres</div>
          </div>


          <div class="form-group"><button class="btn btn-primary btn-block" type="submit">Registrar Examen</button></div></form>
  </div>
</div>
