import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  loginForm!:FormGroup;
  constructor(private fb:FormBuilder) { }

  ngOnInit(): void {
  }
  initForm(){
    return this.fb.group({
      email:['',[Validators.email,Validators.required]],
      password:[]
    })
  }

}
<div class="register-photo">
  <div class="form-container">
      <div class="image-holder" ></div>
      <form [formGroup]="loginForm">
          <h2 class="text-center"><strong>Inicie</strong> sesion</h2>
          <form >
          <div class="form-group"><input class="form-control" type="email" name="email" placeholder="Email"></div>
          <div class="form-group"><input class="form-control" type="password" name="password" placeholder="Contraseña"></div>
          <div class="form-group">
              <div class="form-check"><label class="form-check-label"><input class="form-check-input" type="checkbox">Acepta términos y condiciones</label></div>
          </div>
          <div class="form-group"><button class="btn btn-primary btn-block" type="submit">Iniciar sesion</button></div><a routerLink="/auth/registro"class="already">No tienes una cuenta? registrate ahora.</a></form>
        </form>
    </div>
</div>
